import React from "react";

async function getPlayer(id){

    const url = `https://free-nba.p.rapidapi.com/players/${id}`;
    const options = {
        method: 'GET',
        headers: {
            'X-RapidAPI-Key': '9fa7b755efmshbc9f15a9fe07af2p1ebd85jsne111195ece24',
            'X-RapidAPI-Host': 'free-nba.p.rapidapi.com'
        }
    };

    try {
        const response = await fetch(url, options);
        const result = await response.text();
        console.log(result);
        return (
            response.firstName + ' ' + response.lastName + ' #' + response.team.id

        );
    } catch (error) {
        console.error(error);
    }
}

const Card = (props) => {
    return(
        <div className="bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5">
            <img alt='robots' src={`https://robohash.org/${props.id}?50x50`} />
            <div>
                <h2>{props.name}</h2>
                <p>{props.email}</p>
            </div>
        </div>

    );
}

export default Card;
